# Create views and related function for easier querying...

# The "view_now" view queries the database using the current time/date and returns the creatures that are available now
CREATE VIEW `view_now` AS
	SELECT c.creature_name AS Name, t.type_name AS Type, c.creature_desc AS Description, c.creature_bells AS Bells, c.creature_rate AS 'Appear Rate',
		CASE
			WHEN sched_allday = 1 THEN CONCAT('all day')
			ELSE CONCAT(time_format(sched_starttime, '%l%p'), '-', time_format(sched_endtime, '%l%p'))
		END AS Time
	FROM creature AS c, schedule AS s, type AS t
	WHERE c.creature_id = s.creature_id
	AND c.type_id = t.type_id
	AND s.cal_id = month(now())
	AND (s.sched_allmonth = 1 OR
		 (s.sched_startday <= dayofmonth(now()) AND s.sched_endday >= dayofmonth(now())))
	AND	(s.sched_allday = 1 OR (time(now()) BETWEEN s.sched_starttime AND s.sched_endtime
		OR (s.sched_starttime > s.sched_endtime 
			AND NOT time(now()) BETWEEN s.sched_endtime AND s.sched_starttime)))
	ORDER BY c.creature_name;

# Helper function to concatenate multiple schedule details so they can be returned as a single attribute in MySQL
DELIMITER $$
CREATE FUNCTION `concat_month_sched` (month_num INTEGER, creature_num INTEGER)
RETURNS VARCHAR(45)
DETERMINISTIC
BEGIN
	DECLARE conc_sched VARCHAR(45);
	SELECT
		GROUP_CONCAT(
			CASE
				WHEN sched_allmonth = 1 THEN CONCAT('all month')
				ELSE CONCAT(month_num, '/', sched_startday, '-', month_num, '/', sched_endday) 
			END,
			CASE
				WHEN sched_allday = 1 THEN CONCAT(': all day')
				ELSE CONCAT(': ', time_format(sched_starttime, '%l%p'), '-', time_format(sched_endtime, '%l%p'))
			END
			separator ', ')
		INTO conc_sched
		FROM schedule
		WHERE creature_id = creature_num
		AND cal_id = month_num;
RETURN conc_sched;
END$$

# The "view_all" view returns schedule details for all creatures from the database
CREATE VIEW `view_all` AS
	SELECT c.creature_name AS Name, t.type_name AS Type, c.creature_desc AS Description, c.creature_bells AS Bells, c.creature_rate AS 'Appear Rate',
	concat_month_sched(1, c.creature_id) AS January, 
    concat_month_sched(2, c.creature_id) AS February, 
    concat_month_sched(3, c.creature_id) AS March, 
    concat_month_sched(4, c.creature_id) AS April, 
    concat_month_sched(5, c.creature_id) AS May, 
    concat_month_sched(6, c.creature_id) AS June, 
    concat_month_sched(7, c.creature_id) AS July, 
    concat_month_sched(8, c.creature_id) AS August, 
    concat_month_sched(9, c.creature_id) AS September, 
    concat_month_sched(10, c.creature_id) AS October, 
    concat_month_sched(11, c.creature_id) AS November, 
    concat_month_sched(12, c.creature_id) AS December, 
		(SELECT 
			GROUP_CONCAT(
				CASE
					WHEN sched_allday = 1 THEN CONCAT('all day')
					ELSE CONCAT(time_format(sched_starttime, '%l%p'), '-', time_format(sched_endtime, '%l%p'))
				END
				separator ', ')
			FROM schedule
			WHERE creature_id = c.creature_id
			AND cal_id = 13
		) AS Island
	FROM creature AS c, type AS t
	WHERE c.type_id = t.type_id
	ORDER BY c.creature_name;

# The "view_island" view returns the schedules of all creatures available on the island
CREATE VIEW `view_island` AS
	SELECT c.creature_name AS Name, t.type_name AS Type, c.creature_desc AS Description, c.creature_bells AS Bells, c.creature_rate AS 'Appear Rate',
		CASE
			WHEN sched_allday = 1 THEN CONCAT('all day')
			ELSE CONCAT(time_format(sched_starttime, '%l%p'), '-', time_format(sched_endtime, '%l%p'))
		END AS Time
	FROM creature AS c, schedule AS s, type AS t
	WHERE c.creature_id = s.creature_id
	AND c.type_id = t.type_id
	AND s.cal_id = 13
	ORDER BY c.creature_name;